import React, {Component} from 'react';

import Drawer from 'material-ui/Drawer';
 

const propTypes = {
};

const defaultProps = {
};


class MainDrawer extends Component {

  state = {
    drawer_opened: false
  }

  constructor(props) {
    super(props);

    this.state.drawer_opened = props.uri == 'menu/';
  }

  componentWillReceiveProps(nextProps){

    this.state.drawer_opened = nextProps.drawer_opened;

    return true;
  }


  render() {

    return (
      <Drawer
          docked={true}
          open={this.state.drawer_opened || true}
          onRequestChange={this.props.documentActions.ToggleDrawer}
      >
        {this.props.children}
      </Drawer>
    );
  }
}


MainDrawer.propTypes = propTypes;
MainDrawer.defaultProps = defaultProps;

 

export default MainDrawer