import React, {Component} from 'react';

import PropTypes from 'prop-types';

import Typography from 'material-ui/Typography';

import WarningIcon from 'material-ui-icons/Warning';


export default class WarningField extends Component{

	static defaultProps = {
		children: PropTypes.string.isRequiredm
	};

	render(){

		let{
			children
		} = this.props;

		if(!children){
			return null
		}

		return <Typography 
  		type="title"
  		className="flex align-center"
  	>
  		<WarningIcon color="#FF9800"/> {children}
  	</Typography>;
	}
}