import React, { Component } from 'react';
import ReactDOM from 'react-dom';

 

const defaultProps = {
};

import TopicsView from '../topics';

export default class MainpageView extends TopicsView {

  constructor(props){

    super(props);

    Object.assign(this.state, {
      showFilters: false,
    });
  }

  getTitle(){

    return "MODX-Клуб";
  }

  render(){

    this.state.articles = this.props.document.document.topics || [];

    return super.render();
  }
}
 