export default class CommentView extends CommentsView {
 

  constructor(props){

    super(props);

    Object.assign(this.state, {
      showCommentLink: false,
    });
  }

  
  setPagetitle(){

    let {
      document
    } = this.props.document;

    let {
      data,
    } = document || {};

    let {
      object: comment,
    } = data || {}

    let {
      resource_pagetitle,
    } = comment || {};

    // console.log("comment", comment);

    return super.setPagetitle(`Комментарий к топику ${resource_pagetitle}`);
  }

 

  contentBlock(){


    let {
      classes,
      user,
    } = this.props;

    let {
      document
    } = this.props.document;

    let {
      data,
    } = document || {};

    let {
      object: comment,
    } = data || {};

    if(!comment){

      return null;
    }

    return <div>
      {this.renderComment(comment)}
    </div>
  }
 
}

